% displacement for labels
dx = 0.1; dy = 0.1;
textLabels = cellstr( num2str(flyNum));
text( angularSpeed+dx ,normVisualTuningChange+ dy ,     textLabels)

% linear regression
fitModel = fitlm( angularSpeed,  normVisualTuningChange);
Rsquared = fitModel.Rsquared.Ordinary;
pvalOfSlope = fitModel.Coefficients.pValue(2);

xlabel(' 2 bar closed loop modulation (mV)')
ylabel(' normalized visual change: (sum(abs( normalized after - normalized before) ');
title( [ 'Angular speed vs normalized visual tuning change. R^2:' num2str(Rsquared)   ' pval:' num2str(pvalOfSlope) ])

